class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
                temporadas: [],
                isLoaded: false,
        }
    }

    componentDidMount() {
         fetch('https://tarea-1-breaking-bad.herokuapp.com/api/episodes?series=Better+Call+Saul',
      {
        method: "GET",

      }
    )
        .then(res => res.json())
        .then(resultado_json => {
          console.log(resultado_json,)
            this.setState({
                isLoaded: true,
                temporadas: resultado_json
            })
        });
    }

    render() {
        var {isLoaded, temporadas} = this.state;

        if (!isLoaded) {
            return <div> Loading...</div>;
        }
        else {
            return (
                <div >
                    Data has been loaded
                </div>
            );
        }

    }




}

export default App;
