import React, { Component } from 'react';


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
                temporadas: [],
                isLoaded: false,
        }
    }


    componentDidMount() {
         fetch('https://tarea-1-breaking-bad.herokuapp.com/api/episodes?series=Better+Call+Saul',
      {
        method: "GET",

      }
    )
        .then(res => res.json())
        .then(resultado_json => {
          var temporada = 0;
          for (let i=0; i<resultado_json.length; i+=1)
          {
            var temporadas_provisoria = resultado_json[i].season;
            if (parseInt(temporadas_provisoria) > parseInt(temporada)){
              temporada = temporadas_provisoria;
            }

          }
          var temp_bcs = temporada;
          console.log(temp_bcs);
          console.log("holaa");
            this.setState({
                isLoaded: true,
                temporadas: temp_bcs
            })
        });
    }



    render() {
        var {isLoaded, temporadas} = this.state;

        if (!isLoaded) {
            return <div> Loading...</div>;
        }
        else {
            return (
                <div >
                    Data has been loaded
                </div>
            );
        }

    }




}

export default App;
